import axios from "axios";
import { useEffect,useState } from "react"
import { useNavigate } from "react-router-dom";


export default function YourSecret(){
const navigate = useNavigate();


const [secret,setSecret] = useState([]);

useEffect(()=>{
    async function show(){
        const token= localStorage.getItem('secret-app');
        if(!token)navigate("/");
        const data = await axios.post('http://localhost:5000/secret/show/user',{token:token});

        setSecret(data.data);
    }
    const check= localStorage.getItem('secret-app');
    if(!check){
        navigate('/');
    }
show();
},[secret]);
return(
    <div className="bg-black h-screen">
        <h1 className="bg-gray-dark h-[10vh] text-white font-pacifico text-[5vh] flex justify-center m-auto">Your Secrets</h1>
       <div className="flex flex-row justify-end"> <button className="text-white hover:bg-gray hover:text-black h-[5vh]  bg-dark-blue w-[5vw] px-[2vh] mr-[1vh]">delete</button><button className="text-white hover:bg-gray hover:text-black h-[5vh]  bg-dark-blue w-[5vw] px-[2vh]" onClick={()=>{localStorage.removeItem('secret-app'); navigate("/")}}>Logout</button></div>
        {
        secret.map((x,i)=>{
         return (<div className="h-[40vh] w-[100vw] bg-dark-blue my-[5vh]">
            <h1 className="text-white text-[5vh] flex justify-center border border-green h-[8vh] overflow-auto">{x.category}</h1>

            <h1 className="text-white text-[3vh] my-[2vh] border border-green h-[30vh] overflow-auto">{x.secret}</h1>

            </div>)
        })
        }
    </div>
);



}
