import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom"

export default function Category(){
    let {title} = useParams();
    const [secrets,setSecrets] = useState([]);
    let navigate = useNavigate();
    useEffect(()=>{
        async function getSecret(){
            try{
         const res = await axios.post("http://localhost:5000/secret/show/category",{category:title});
         setSecrets(res.data);
            }
            catch(err){
                console.log(err);
                alert(err.response.data);
            }

        }
        const check = localStorage.getItem('secret-app');
        if(!check){
            navigate('/');
        }
        getSecret();

    },[secrets]);

    return (
        <div className="bg-black">
        {
        secrets.map((x,i)=>{
         return (<div className="h-auto  w-[100vw] bg-dark-blue my-[5vh]">
            <h1 className="text-white text-[4vh] flex justify-center border border-green h-auto overflow-auto">{x}</h1>
            </div>)
        })
        }
        </div>
    )
}
