           localStorage.setItem('SECRET-APP',token);
        }
        catch(e){
           console.log(e);
        }
      }
    return(
       <div className="h-screen  flex align-middle justify-center bg-black">
       <div className="h-3/5 w-2/6 mr-[200px] bg-dark-blue mt-[100px] flex flex-col justify-around   ">
          <h1 className="font-pacifico lg:text-7xl sm:text-4xl md:text-6xl h-auto bg-black text-white w-full lg:px-36 sm:px-10 lg:mx-auto sm:mx-auto md:mx-auto">Secret</h1>
          <h1 className="font-pacifico lg:text-4xl md:text-3xl sm:text-2xl mx-auto">Login</h1>
          <label className="font-pacifico lg:text-4xl  md:text-3xl sm:text-2xl mx-5">
         Email : <input className="h-10 w-32 lg:w-60 md:w-44 mx-4 my-10  lg:ml-20 sm:ml-1 md:ml-5" onChange ={(e)=>{setEmail(e.target.value)}} />
      </label>
      
      <label className="font-pacifico lg:text-4xl md:text-3xl  sm:text-2xl  mx-5">
         Password : <input className="h-10 w-32 lg:w-60 md:w-44 mx-4" onChange ={(e)=>{setPassword(e.target.value)}} />
      </label>
      <button className="hover:bg-green bg-white h-auto w-10 mx-auto" onClick={Submit}>Click</button>
      <h1 className="text-white font-pacifico mx-auto">Already had Account. Please Login!</h1>
     </div>
 
     </div>
     
    )
 
 }
