import {React,useEffect,useState} from 'react';
import axios from 'axios';
export default function View(){

    const [conf,setConf] = useState([]);

    useEffect(()=>{
     async function call(){
      const get_categories =  await axios.get('http://localhost:5000/secret/category');
      console.log(get_categories);
      setConf(get_categories.data);
     }
     call();
    },[conf]);
    //conf=[{id:"1",category:"theft"},{id:"2",category:"thefts"},{id:"2",category:"thefts"},{id:"2",category:"thefts"},{id:"2",category:"thefts"},{id:"2",category:"thefts"},{id:"2",category:"thefts"},{id:"3",category:"theftd"},{id:"4",category:"thefties"}];

    return(
        <div >
    <div className='h-[10vh] bg-gray-dark flex text-black align-middle justify-around mx-auto'>
        <button className='h-[5vh] w-auto hover:bg-green bg-white px-2 m-auto'>Post</button>
        <button className='h-[5vh] w-auto hover:bg-green bg-white px-2 m-auto'>Your Secret </button>
        <button className='h-[5vh] w-auto hover:bg-green bg-white px-2 m-auto'>Logout</button>
      
    </div>
    <div className='flex flex-col bg-black h-[90vh] overflow-auto '>
  <h1 className='text-3xl md:text-4xl lg:text-5xl text-white flex justify-center'>
    Categories
  </h1>
  <div>
    {
      conf.map((x,i)=>{
        return <button className='bg-black h-10 hover:bg-purple  border border-blue text-white bord w-full my-5'>{x.category}</button>
      })
    }
  </div>
  
  

  </div>
    </div>

    )








}
